  const {dispatch} = useContext(AppContext);

    const changeCurrency = (val)=>{
            dispatch({
                type: 'CHG_CURRENCY',
                payload: val,
            })
    }


  return (
    <div className="dropdown">
    <button className="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
      Currency
    </button>
    <div className="dropdown-menu" aria-labelledby="dropdownMenuButton">
      <button className="dropdown-item" onClick={() => handleSelect("$")}>$Dollar</button>
      <button className="dropdown-item" onClick={() => handleSelect("£")}>£Pound</button>
      <button className="dropdown-item" onClick={() => handleSelect("€")}>€Euro</button>
      <button className="dropdown-item" onClick={() => handleSelect("₹")}>₹Ruppee</button>
    </div>
  </div>
);


};

export default Currency;
